import React, {useState} from "react";
import {Button, Card, Col, Container, Form, ListGroup, Row} from "react-bootstrap";
import DateTimePicker from 'react-datetime-picker';

import "react-datepicker/dist/react-datepicker.css";

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            name: "",
            surname: "",
            phone: "",
            email: "",
            street: "",
            houseNumber: "",
            city: "",
            plate: "",
            date: "",
            problems: []
        };
        this.labels = {
            name: "Jméno",
            surname: "Příjmení",
            phone: "Tel. číslo",
            email: "Email",
            street: "Ulice",
            houseNumber: "Číslo domu",
            city: "Město",
            plate: "SPZ",
        }
        this.errors = 0
        this.button = true
    }

    onChange = (e, name) => {
        this.checkValue(e, name)
        console.log(this.state)
        this.setState({
            [name]: e
        })
        this.setButton()
    }

    setButton = () => {
        if (this.errors > 0){
            this.button = true
        }else {
            this.button = false
        }
    }

    checkValue = (text, name) => {
        switch (name) {
            case "name":
            case "surname":
            case "street":
            case "city": {
                this.checkText(text, name)
                break;
            }
            case "phone": {
                this.checkPhone(text, name)
                break;
            }
            case "email": {
                this.checkEmail(text, name)
                break;
            }
            case "houseNumber": {
                this.checkHouseNumber(text, name)
                break;
            }
            case "plate": {
                this.checkPlate(text, name)
                break;
            }
            default: {
                this.setState({
                    [name]: text
                })
            }
        }
    }

    checkText = (text, name) => {
        if (!/^[a-záčďěéíňóřšťúůýž]+$/.test(text.toLowerCase())) {
            this.setLabel(text, name, true)
        } else {
            this.setLabel(text, name, false)
        }
    }

    checkPhone = (text, name) => {
        if (!/^\+[0-9]{12}$/.test(text)) {
            this.setLabel(text, name, true)
        } else {
            this.setLabel(text, name, false)
        }
    }

    checkEmail = (text, name) => {
