import React from "react";
import {Button, Card, Col, Container, Form, ListGroup, Row} from "react-bootstrap";
import DateTimePicker from 'react-datetime-picker';


class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            loading: true,
            data: [],
            error: null,
            date: new Date(),
            description: "",
            price:0,
            id: "",
            updateDate: new Date(),
            updatedPrice:0,
            updateDescription: ""
        };
    }

    onChange = (e, name) => {
        //console.log("---", e, name)
        this.setState({
            [name]: e
        })
    }


    render() {

        return (
            <Container>
                <h1>React REST Demo</h1>
                <Card>
                    <Card.Header>Create</Card.Header>
                    <Card.Body>
                        <Card.Title>Create new item</Card.Title>
                        <Form>
                            <Form.Group controlId="nameId">
                                <Form.Control
                                    type="text"
                                    placeholder="Enter description"
                                    value={this.state.description}
                                    onChange={(e) => this.onChange(e.target.value, 'description')}
                                />
                            </Form.Group>
                            <Form.Group controlId="startWorkId">
                                <Form.Label>date</Form.Label>
                                <DateTimePicker
                                    onChange={(e) => this.onChange(e, 'date')}
                                    value={this.state.date}
                                />
                            </Form.Group>
                            <Form.Group controlId="endWorkId">
                                <Form.Label>price</Form.Label>
                                <Form.Control
                                    type="text"
                                    placeholder="Enter price"
                                    value={this.state.price}
                                    onChange={(e) => this.onChange(e.target.value, 'price')}
                                />
                            </Form.Group>
                            <Button variant="primary" onClick={this.createNew} type="submit">Create</Button>
                        </Form>
                    </Card.Body>
                </Card>
            </Container>
        );
    }
}

export default App;
